<template xmlns:text-decoration="http://www.w3.org/1999/xhtml">    <div class="popup_wrapper">        <div class="popup">            <div class="popup__header">                <span>{{ popupTitle }}</span>            </div>            <div class="popup__content">                <slot></slot>            </div>            <div class="popup__footer">                <b-button variant="outline-dark" size="sm"                          class="pop_btn"                          @click="leftButtonAction"                          x-small                >                    {{ leftButtonTitle }}                </b-button>                <b-button variant="outline-dark" size="sm"                          class="pop_btn"                          x-small                          @click="closePopup"                >                    Отмена                </b-button>            </div>        </div>    </div></template><script>    export default {        name: "Popup",        props: {            popupTitle: {                type: String,                default: 'Заголовок'            },            leftButtonTitle: {                type: String,                default: 'Ok'            }        },        data() {            return {                file: ''            }        },        methods: {            leftButtonAction() {                this.$emit('leftButtonAction')            },            closePopup() {                this.$emit('closePopup')            }        }    }</script><style lang="scss">    .popup_wrapper {        background: rgba(64,64,64, .4);        display: flex;        justify-content: center;        align-items: center;        position: absolute;        right: 0;        left: 0;        top: 0;        botton: 0;    }    .popup {        padding: 16px;        position: fixed;        top: 300px;        justify-content: center;        width: 780px;        background: #ffffff;        box-shadow: 0 0 17px 0;        z-index: 50;        &__header {            display: flex;            justify-content: space-between;            align-items: center;        }        &__footer {            display: flex;            justify-content: space-between;            align-items: center;        }        &__content {            display: flex;            justify-content: center;            align-items: center;        }    }    .pop_btn {        margin: 10px 5px 5px 0;        text-align: center;        width: 110px;        font-size: smaller;    }</style>