<template>    <div class="mPage">        <b-container>            <b-row>                <b-col sm="3 justify-content-start d-flex">                    <b-button class="ls_btn"                              @click="createNewMetroLine"                              variant="outline-primary"                              size="lg"                              ref="entry">                        Создать                        <b-icon icon="clipboard-plus" font-scale="1.2" shift-v="-1" shift-h="2"></b-icon>                    </b-button>                </b-col>                <b-col sm="9 justify-content-end d-flex">                    <b-button class="long_btn"                              @click="editMetroLine"                              variant="outline-dark"                              size="lg"                              :disabled="editState"                              ref="entry">                        Редактировать                        <b-icon-file-earmark-code font-scale="1.2" shift-v="-1"                                                  shift-h="2"></b-icon-file-earmark-code>                    </b-button>                    <b-button class="del_btn"                              @click="showDeletePopup"                              variant="outline-dark"                              size="lg"                              :disabled="deleteState"                              ref="entry">                        Удалить                        <b-icon icon="trash" font-scale="1.2" shift-v="-2" shift-h="2"></b-icon>                    </b-button>                </b-col>            </b-row>            <popup                    v-if="isDeletePopup"                    leftButtonTitle="Удалить"                    popupTitle="Удаление"                    @closePopup="closeDeletePopup"                    @leftButtonAction="deleteLine"            >                <span> Вы действительно хотите удалить запись?</span>            </popup>            <div>                <b-toast id="success-delete-toast" variant="success" solid :append-toast=true>                    <template v-slot:toast-title>                        <div class="d-flex flex-grow-1 align-items-baseline">                            <b-img blank blank-color="#8FBC8F" class="mr-2" width="12" height="12"></b-img>                            <strong class="mr-auto">Успешно!</strong>                        </div>                    </template>                    {{ this.message }}                </b-toast>                <b-toast id="danger-delete-toast" variant="danger" solid :append-toast=true>                    <template v-slot:toast-title>                        <div class="d-flex flex-grow-1 align-items-baseline">                            <b-img blank blank-color="#8B0000" class="mr-2" width="12" height="12"></b-img>                            <strong class="mr-auto">Ошибка!</strong>                        </div>                    </template>                    {{ this.message }}                </b-toast>            </div>        </b-container>    </div></template><script>    import Popup from '../components/Popup'    import axios from 'axios'    export default {        name: "EditorMenu",        props: ['lineId',            'deleteState',            'editState',            'isBusy'        ],        components: {            Popup,        },        data() {            return {                isDeletePopup: false,                isBusyState: this.isBusy,                message: '',            }        },        methods: {            showDeletePopup() {                this.isDeletePopup = true;                this.busy = true            },            closeDeletePopup() {                this.isDeletePopup = false;                this.busy = false            },            deleteLine() {                this.busy = true                let id = this.lineId["id"]                console.log('Line id = ' + id)                axios.delete('/api/api/editor/' + id + '/delete',                ).then(() => {                    this.message = "Запись удалена"                    this.$bvToast.show('success-delete-toast')                    this.closeDeletePopup()                }).catch(() => {                    this.message = "Не удалось удалить запись!"                    this.$bvToast.show('danger-delete-toast')                }).finally(() => {                    this.$emit('delete-line')                    this.busy = false                })            },            editMetroLine() {                let id = this.lineId["id"]                this.$router.push({name: 'editor', params: {Pid: id,}})            },            createNewMetroLine() {                this.$router.push({name: 'creator', params: {Pid: 0}})            }        }    }</script><style scoped>    .mPage {        margin: 10px 10px 10px 10px;        font-size: large;    }    .long_btn {        margin: 10px 0 20px 10px;        text-align: center;        width: 190px;        font-size: medium;    }    .ls_btn {        margin: 10px 0 20px 10px;        text-align: center;        width: 140px;        font-size: medium;    }    .del_btn {        margin: 10px 0 20px 10px;        text-align: center;        width: 140px;        font-size: medium;    }</style>